const container=document.getElementById("reading-status-container");let bar=document.createElement("div");function updateBar(){var t=100*(window.scrollY/(container.clientHeight-window.innerHeight));bar.style.width=t+"%"}bar.style.height="2px",bar.style.width="0",bar.style.backgroundColor="#EA1017",bar.style.position="fixed",bar.style.top="0px",bar.style.left="0",bar.style.zIndex="9999",bar.style.transition="0.1s",document.body.append(bar),window.addEventListener("load",()=>{document.addEventListener("scroll",updateBar)});var prevScrollpos=window.pageYOffset;window.onscroll=function(){var t=window.pageYOffset;document.getElementById("nav-wrapper").style.top=prevScrollpos>t?"0":"-100px",prevScrollpos=t};var btn=$("#to-top-button");$(window).scroll(function(){$(window).scrollTop()>200?btn.addClass("show"):btn.removeClass("show")}),btn.on("click",function(t){t.preventDefault(),$("html, body").animate({scrollTop:0},"200")});